import { useEffect, useState } from 'react';
import axios from 'axios';

function YourComponent() {
  const [userInfo, setUserInfo] = useState(null);

  useEffect(() => {
    const authToken = document.cookie.replace(/(?:(?:^|.*;\s*)authToken\s*=\s*([^;]*).*$)|^.*$/, '$1');

    if (authToken) {
      // Make a fetch request to your PHP endpoint to get user data based on the JWT token
      axios.get('user/info.php', {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      })
        .then(response => {
          // Assuming the response contains a 'user' property
          setUserInfo(response.data.user);
        })
        .catch(error => console.error('Error fetching user data:', error));
    }
  }, []);

  return (
    <div>
      {/* Render your component using the user information */}
      {userInfo && (
        <div>
          <h1>User Information</h1>
          <p>Username: {userInfo.username}</p>
          {/* Add other user information fields as needed */}
        </div>
      )}
    </div>
  );
}

export default YourComponent;
