import React, { useState, useEffect } from 'react';

function Panel() {
    const [userInfo, setUserInfo] = useState(null);

    useEffect(() => {
        // Get the JWT token from cookies
        const authToken = document.cookie.replace(/(?:(?:^|.*;\s*)authToken\s*=\s*([^;]*).*$)|^.*$/, '$1');

        if (authToken) {
            // Make a fetch request to your PHP endpoint to get user data based on the JWT token
            fetch('user/user.php', {
                headers: {
                    Authorization: `Bearer ${authToken}`,
                },
            })
                .then(response => response.json())
                .then(data => {
                    // Set the user information based on the response
                    setUserInfo(data.user);
                })
                .catch(error => console.error('Error fetching user data:', error));
        }
    }, []);

    function exit() {
        document.cookie = `authToken=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`;
        window.location.reload();
    }

    return (
        <div className="panel">
            <h1>Панель управления</h1>
            {userInfo && (
                <div>
                    <span>Логин: {userInfo.login}</span>
                    <span>Дата создания: {userInfo.created_at}</span>
                </div>
            )}
            <button onClick={exit}>Выйти</button>
        </div>
    );
}

export default Panel;
