import React, { useState, useEffect } from "react";
import axios from 'axios';
import { useParams } from 'react-router-dom';
import b8 from '../../img/b8.png';

const ProfileContent = () => {
    const { login } = useParams();
    const [userData, setUserData] = useState(null);
    const [fileInfoList, setFileInfoList] = useState([]);

    useEffect(() => {
        // Fetch user information
        axios.get(`/user/info.php?login=${login}`)
            .then(response => {
                console.log(response)
                if (response.data.status == "success") {
                    setUserData(response.data);
                }
            })
            .catch(error => {
                console.error('Error fetching user information:', error);
            });

        // Fetch file information
        axios.get(`/files/info.php?login=${login}`)
            .then(response => {
                console.log(response)
                if (response.status == 200) {
                    console.log(response.data.files)
                    setFileInfoList(response.data.files);
                }


            })
            .catch(error => {
                console.error('Error fetching file information:', error);
            });
    }, [login]);

    return (
        <div className="relative bg-[#EFEFEF] w-full h-screen rounded-tl-[80px] p-[50px]">
            <div className="p-[30px] bg-white rounded-[20px] w-100 h-100">
                {userData && (
                    <div className="flex items-center" style={{ gap: "20px" }}>
                        <div className="w-[100px] h-[100px] flex items-center justify-center">
                            <img src={userData.logo} alt="" className="max-w-[100px] max-h-[100px]" />
                        </div>
                        <h4 className="text-2xl font-semibold">{userData.login}</h4>

                        <h5 class="text-slate-500" style={{
                            position: "relative",
                            top: "-50px",
                            left: "400px"
                        }}>История отчетов</h5>
                    </div>
                )}
                {!userData && (
                    <div>
                        <h4 className="text-2xl font-semibold">Такой профиль не найден</h4>
                        {/* Display other user information as needed */}
                    </div>
                )}
                {fileInfoList && (
                    <div className="px-[2em]">
                        <div className="flex justify-center">
                            <ul className="flex gap-[150px] text-[30px] text-[#818181] font-semibold justify-around" >
                                <li>Название</li>
                                <li>Размер</li>
                                <li>Дата</li>
                                <li>Статус</li>
                            </ul>
                        </div>
                        <div className="flex justify-center">
                        <ul className="bg-[#F5F5F5] rounded-[36px] mt-[10px] flex gap-[150px] text-[30px] text-[#818181] font-semibold justify-around">
                            {fileInfoList.map(fileInfo => (
                                <>
                                    <li>
                                        {fileInfo.login}
                                    </li>
                                    <li>
                                        {fileInfo.size_mb}
                                    </li>
                                    <li>
                                        {fileInfo.time}
                                    </li>
                                    <li>
                                        {fileInfo.status}
                                    </li>
                                </>
                            ))}
                        </ul>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default ProfileContent;
